pragma solidity >=0.4.20;

contract Election {
    // model a candidate
    struct Candidate {
        uint id;
        string name;
        uint voteCount;
    }

    // store candidates
    // fetch candidates
    mapping(uint => Candidate) public candidates; // solidity will give us getters and setters

    // store candidates count => not possible to loop through the above mapping, since the above mappings will return empty structs
    uint public candidatesCount;

    constructor() public {
        addCandidate("candidate 1");
        addCandidate("candidate 2");
    }

    function addCandidate(string memory _name) private {
        candidatesCount++;
        candidates[candidatesCount] = Candidate(candidatesCount, _name, 0);
    }
}
